import React, { useEffect, useState } from 'react';
import {
  Container,
  Typography,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  CircularProgress,
  Grid,
  Paper,
  List,
  ListItem,
  ListItemText,
  Divider,
  Alert
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import MonetizationOnIcon from '@mui/icons-material/MonetizationOn';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Expenses = () => {
  const { id: eventId } = useParams();

  const [expenses, setExpenses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [form, setForm] = useState({ title: '', amount: '', paidBy: '' });
  const [error, setError] = useState('');

  const fetchExpenses = async () => {
    try {
      const res = await axios.get(`/api/expenses/event/${eventId}`);
      setExpenses(res.data);
    } catch (err) {
      console.error('Failed to fetch expenses', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchExpenses();
  }, [eventId]);

  const handleOpenDialog = () => {
    setForm({ title: '', amount: '', paidBy: '' });
    setError('');
    setDialogOpen(true);
  };

  const handleCloseDialog = () => setDialogOpen(false);

  const handleChange = (e) => {
    setForm(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async () => {
    if (!form.title || !form.amount || !form.paidBy) {
      setError('All fields are required');
      return;
    }

    try {
      await axios.post('/api/expenses', { ...form, event: eventId });
      setDialogOpen(false);
      fetchExpenses();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to add expense');
    }
  };

  const total = expenses.reduce((sum, exp) => sum + exp.amount, 0);

  return (
    <Container maxWidth="md" sx={{ mt: 5 }}>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 'bold' }}>
        <MonetizationOnIcon sx={{ mr: 1, color: 'green' }} />
        Manage Expenses
      </Typography>

      <Grid container justifyContent="space-between" alignItems="center" sx={{ mb: 3 }}>
        <Grid item>
          <Typography variant="h6" color="textSecondary">
            Total Expenses: â‚¹{total.toFixed(2)}
          </Typography>
        </Grid>
        <Grid item>
          <Button variant="contained" startIcon={<AddIcon />} onClick={handleOpenDialog}>
            Add Expense
          </Button>
        </Grid>
      </Grid>

      {loading ? (
        <CircularProgress />
      ) : expenses.length === 0 ? (
        <Typography>No expenses added yet.</Typography>
      ) : (
        <Paper elevation={3} sx={{ borderRadius: 2 }}>
          <List>
            {expenses.map((exp, i) => (
              <React.Fragment key={exp._id}>
                <ListItem>
                  <ListItemText
                    primary={`${exp.title} - â‚¹${exp.amount.toFixed(2)}`}
                    secondary={`Paid by: ${exp.paidBy}`}
                  />
                </ListItem>
                {i !== expenses.length - 1 && <Divider />}
              </React.Fragment>
            ))}
          </List>
        </Paper>
      )}

      <Dialog open={dialogOpen} onClose={handleCloseDialog} maxWidth="sm" fullWidth>
        <DialogTitle>Add New Expense</DialogTitle>
        <DialogContent sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
          {error && <Alert severity="error">{error}</Alert>}
          <TextField
            label="Title"
            name="title"
            value={form.title}
            onChange={handleChange}
            fullWidth
            required
          />
          <TextField
            label="Amount (â‚¹)"
            name="amount"
            type="number"
            value={form.amount}
            onChange={handleChange}
            fullWidth
            required
          />
          <TextField
            label="Paid By"
            name="paidBy"
            value={form.paidBy}
            onChange={handleChange}
            fullWidth
            required
            placeholder="Username or name"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog}>Cancel</Button>
          <Button onClick={handleSubmit} variant="contained">Add</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Expenses;
