import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Button,
  CircularProgress,
  Paper,
  Box
} from '@mui/material';
import axios from 'axios';

const EventDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`/api/events/${id}`)
      .then(res => {
        setEvent(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [id]);

  if (loading) {
    return (
      <Container sx={{ mt: 6, textAlign: 'center' }}>
        <CircularProgress />
      </Container>
    );
  }

  if (!event) {
    return (
      <Container sx={{ mt: 6 }}>
        <Typography variant="h6" color="error">Event not found.</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="md" sx={{ mt: 6 }}>
      <Paper elevation={3} sx={{ p: 4, borderRadius: 3, backgroundColor: '#f9f9f9' }}>
        <Typography variant="h4" gutterBottom sx={{ fontWeight: 600 }}>
          {event.title}
        </Typography>

        <Typography variant="subtitle2" color="textSecondary" gutterBottom>
          Organized by: {event.organizer?.username || 'Unknown'}
        </Typography>

        <Typography variant="subtitle1" gutterBottom>
          {new Date(event.startDate).toLocaleString()} — {new Date(event.endDate).toLocaleString()}
        </Typography>

        <Typography variant="body1" sx={{ mb: 2 }}>
          {event.description || 'No description provided.'}
        </Typography>

        <Box sx={{ mb: 2 }}>
          <Typography variant="body2"><strong>Type:</strong> {event.type}</Typography>
          <Typography variant="body2"><strong>Location:</strong> {event.location || 'N/A'}</Typography>
          <Typography variant="body2"><strong>Budget:</strong> ₹{event.budget || 0}</Typography>
          <Typography variant="body2"><strong>Tags:</strong> {event.tags?.join(', ') || 'None'}</Typography>
        </Box>

        <Button
          variant="contained"
          color="primary"
          onClick={() => navigate('/events')}
          sx={{ mt: 2, fontWeight: 'bold' }}
        >
          ← Back to Events
        </Button>
      </Paper>
    </Container>
  );
};

export default EventDetails;
