import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardActions,
  Button,
  Box,
  Chip,
  CircularProgress
} from '@mui/material';
import {
  Event,
  People,
  AttachMoney,
  Assignment,
  Add
} from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import dayjs from 'dayjs';

const Dashboard = () => {
  const [events, setEvents] = useState([]);
  const [stats, setStats] = useState({
    totalEvents: 0,
    upcomingEvents: 0,
    totalExpenses: 0,
    pendingTasks: 0
  });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [eventsResponse] = await Promise.all([
        axios.get('/api/events')
      ]);

      const eventsData = eventsResponse.data;
      setEvents(eventsData.slice(0, 6)); // Show only first 6 events

      // Calculate stats
      const now = new Date();
      const upcomingEvents = eventsData.filter(event => 
        new Date(event.startDate) > now
      ).length;

      setStats({
        totalEvents: eventsData.length,
        upcomingEvents,
        totalExpenses: 0, // Will be calculated when expenses are fetched
        pendingTasks: 0 // Will be calculated when tasks are fetched
      });

      setLoading(false);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'planning':
        return 'info';
      case 'active':
        return 'success';
      case 'completed':
        return 'default';
      case 'cancelled':
        return 'error';
      default:
        return 'default';
    }
  };

  const getTypeIcon = (type) => {
    switch (type) {
      case 'trip':
        return '✈️';
      case 'party':
        return '🎉';
      case 'dinner':
        return '🍽️';
      case 'meeting':
        return '📋';
      default:
        return '📅';
    }
  };

  if (loading) {
    return (
      <Container sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
        <CircularProgress />
      </Container>
    );
  }
return (
  <Container maxWidth="lg">
    <Box sx={{ mb: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Dashboard
      </Typography>
      <Typography variant="subtitle1" color="text.secondary">
        Welcome back! Here's what's happening with your events.
      </Typography>
    </Box>

    {/* Stats Cards */}
    <Grid container spacing={3} sx={{ mb: 4 }}>
      {/* ... your stats cards */}
    </Grid>

    {/* Recent Events */}
    <Box sx={{ mb: 4 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h5" component="h2">
          Recent Events
        </Typography>
        <Button
          variant="contained"
          startIcon={<Add />}
          onClick={() => navigate('/events/create')}
        >
          Create Event
        </Button>
      </Box>

      <Grid container spacing={3}>
        {events.length === 0 ? (
          <Typography>No recent events found.</Typography>
        ) : (
          events.map(event => (
            <Grid item xs={12} sm={6} md={4} key={event._id}>
              <Card onClick={() => navigate(`/events/${event._id}`)} sx={{ cursor: 'pointer' }}>
                <CardContent>
                  <Typography variant="h6">
                    {getTypeIcon(event.type)} {event.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {dayjs(event.startDate).format('MMM D, YYYY')} - {dayjs(event.endDate).format('MMM D, YYYY')}
                  </Typography>
                  <Chip label={event.status} color={getStatusColor(event.status)} size="small" sx={{ mt: 1 }} />
                </CardContent>
                <CardActions>
                  <Button size="small" onClick={() => navigate(`/events/${event._id}`)}>
                    View Details
                  </Button>
                </CardActions>
              </Card>
            </Grid>
          ))
        )}
      </Grid>
    </Box>
  </Container>
);

};
