import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Typography, Button, CircularProgress } from '@mui/material';
import axios from 'axios';

const EventDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`/api/events/${id}`)
      .then(res => {
        setEvent(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error(err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <Container sx={{ mt: 4, textAlign: 'center' }}><CircularProgress /></Container>;

  if (!event) return <Container sx={{ mt: 4 }}><Typography>Event not found.</Typography></Container>;

  return (
    <Container sx={{ mt: 4 }}>
      <Typography variant="h4" gutterBottom>{event.title}</Typography>
      <Typography variant="subtitle1" gutterBottom>
        {new Date(event.startDate).toLocaleString()} - {new Date(event.endDate).toLocaleString()}
      </Typography>
      <Typography variant="body1" gutterBottom>{event.description}</Typography>
      <Button variant="contained" onClick={() => navigate('/events')} sx={{ mt: 2 }}>
        Back to Events
      </Button>
    </Container>
  );
};

export default EventDetails;
